pragma solidity >=0.4.26;

contract FundRaising{


    struct request{
        string description;
        address payable recipient;
        uint value;
        bool completed;
        uint noOfVoters;
        mapping(address=>bool) voters;
    }

    address  payable public admin;
    uint public noOfContributors;
    uint public minContributions;
    uint public deadline;
    uint public goal;
    uint public raisedAmonut=0;
    uint public requestsCount=0;

    mapping(uint=>request) requests;  
    mapping(address=>uint) public Contributors;
    mapping(address=>uint)public DirectPays;

    modifier onlyAdmin(){
        require(msg.sender==admin);
        _;
    }


    event ContributeEvent(address sender,uint value);
    event CreateRequestEvent(string  description,address recipient,uint value);
    event MakePaymentEvent(address recipient,uint value);


     constructor() public{
        admin=msg.sender;
        minContributions=1000;

    }



     function createRequest( string memory _description, address payable _recipient,uint _value)public onlyAdmin{

            requests[requestsCount]=request(_description,_recipient,_value,false,0);
            requestsCount++;
            emit CreateRequestEvent(_description,_recipient,_value);
    }


















    function contribute() public payable{

        require(now<deadline);
        require(msg.value>minContributions);
        if(Contributors[msg.sender]==0)
        {
            noOfContributors++;
        }
        raisedAmonut+=msg.value;
        Contributors[msg.sender]+=msg.value;

        emit ContributeEvent(msg.sender,msg.value);
    }
    function getBalance() public view returns(uint){
        return address(this).balance ;

    }


